<?php $this->load->view('header'); ?>  <!-- <link href="<?php echo base_url(); ?>public/css/daterangepicker.css" rel="stylesheet"> --><?php $this->load->view('left_panel'); ?><?php $this->load->view('top_panel'); ?><div class="right_col" role="main">    <?php $this->load->view('content_panel'); ?></div> <?php $this->load->view('homefooter'); ?> <!-- <script src="<?php echo base_url(); ?>public/js/moment.min.js"></script> --><script type="text/javascript" src="//cdn.jsdelivr.net/momentjs/latest/moment.min.js"></script><script type="text/javascript" src="//cdn.jsdelivr.net/bootstrap.daterangepicker/2/daterangepicker.js"></script><link rel="stylesheet" type="text/css" href="//cdn.jsdelivr.net/bootstrap.daterangepicker/2/daterangepicker.css" /><!-- <script src="<?php echo base_url(); ?>public/js/daterangepicker.js"></script> --><script>      function changereport()    {       $duration=$('#duration').val();       if($duration=='')       {                   alert("Select Any One Option!!!!");       }       else{         $('#daterangepickerval').val('');       }        $daterangeval=$('#daterangepickerval').val();        $.ajax({                url: '<?php echo site_url();?>' + '/home/changereport',                 data: 'duration=' + $duration+'&daterangeval=' + $daterangeval,                dataType: 'json',                type: 'POST',                success: function(data)                {                                      $('#accountsummary_result').html(data.accountsummary_result);                    $('#bookingsummary').html(data.bookingsummary);                                }            });    }    function changedaterangereport()    {       $daterangeval=$('#daterangepickerval').val();       if($daterangeval=='')       {         alert("Select Date Range Option!!!!");       }       else       {        $('#duration').val('');       }       $duration=$('#duration').val();         $.ajax({                url: '<?php echo site_url();?>' + '/home/changereport',                data: 'duration=' + $duration+'&daterangeval=' + $daterangeval,                dataType: 'json',                type: 'POST',                success: function(data)                {                                      $('#accountsummary_result').html(data.accountsummary_result);                    $('#bookingsummary').html(data.bookingsummary);                                }            });     }      $(function() {          var dateToday = new Date();            $('#daterangepickerval').daterangepicker({              autoApply: false,        maxDate: dateToday,        stepMonths: false,        timePickerIncrement: 30,        locale: {            format: 'MM/DD/YYYY',             cancelLabel: 'Clear'        }    });      $('#daterangepickerval').on('apply.daterangepicker', function(ev, picker) {      $(this).val(picker.startDate.format('MM/DD/YYYY') + ' - ' + picker.endDate.format('MM/DD/YYYY'));  });  $('#daterangepickerval').on('cancel.daterangepicker', function(ev, picker) {      $(this).val('');  });     });</script><style type="text/css">    .noborder tbody tr td{        border: none;        padding-top: 0;    }</style>